                    <div class=\"py-5 text-center\">
                        <h1 class=\"fw-bold\">{{ article.title }}</h1>
                    </div>
                    
                    <img class=\"img-fluid\" src=\"{{ article.image }}\" alt=\"\">

                    <div class=\"lead mb-4 py-5\">{{ article.text | raw }}</div>

